// Parse AI Agent response and determine delegation
const aiResponse = $input.first().json;
let analysis = aiResponse;

// Try to parse if it's a string
if (typeof aiResponse === 'string') {
  try {
    analysis = JSON.parse(aiResponse);
  } catch (e) {
    analysis = { 
      delegate_to: ['brahma-network'],
      severity: 'medium',
      analysis: aiResponse 
    };
  }
}

return {
  json: {
    issue: $('Webhook - Critical Issue').item.json.body,
    analysis: analysis.analysis,
    severity: analysis.severity || 'medium',
    delegate_to: analysis.delegate_to || [],
    requires_approval: analysis.requires_approval || false,
    proposed_action: analysis.proposed_action || 'Investigate'
  }
};

// Synthesize results from delegated agent
const delegateResult = $input.first().json;
const originalIssue = $('Code - Delegation Logic').item.json;

return {
  json: {
    summary: `üî± Vishnu CTO - Issue Resolution

Original Issue: ${JSON.stringify(originalIssue.issue)}

Severity: ${originalIssue.severity.toUpperCase()}

Delegated To: ${originalIssue.delegate_to.join(', ')}

Specialist Report:
${JSON.stringify(delegateResult, null, 2)}

Decision: ${originalIssue.proposed_action}

Requires Human Approval: ${originalIssue.requires_approval ? 'YES' : 'NO'}`,
    requires_approval: originalIssue.requires_approval,
    proposed_action: originalIssue.proposed_action
  }
};

// Combine network diagnostics data
const unifiData = $('HTTP Request - UniFi Status').first()?.json || { error: 'UniFi unavailable' };
const pingData = $('Execute Command - Net Diagnostics').first()?.json?.stdout || 'Diagnostics failed';

return {
  json: {
    timestamp: new Date().toISOString(),
    unifi_health: unifiData,
    network_diagnostics: pingData,
    summary: `Network check at ${new Date().toLocaleString()}`
  }
};

// Parse AI deployment plan and Plex session data
const plexSessions = $('HTTP Request - Plex Session Check').first()?.json || { size: 0 };
const aiPlan = $input.first().json;
const webhookData = $('Webhook Trigger - CI/CD Push').item.json.body;

let plan = aiPlan;
if (typeof aiPlan === 'string') {
  try {
    plan = JSON.parse(aiPlan);
  } catch (e) {
    plan = { deployment_plan: aiPlan, requires_approval: true };
  }
}

const activeSessions = plexSessions.size || 0;
const canDeploy = activeSessions === 0;

return {
  json: {
    deployment: webhookData,
    plan: plan,
    active_sessions: activeSessions,
    can_deploy: canDeploy,
    requires_approval: plan.requires_approval || !canDeploy,
    docker_command: `docker restart ${webhookData.service || 'plex'}`
  }
};

