// Parse user response from Telegram message
const message = $input.first().json.message;
const text = (message.text || '').trim().toUpperCase();
const userId = message.from.id;
const chatId = message.chat.id;

// Extract approval ID if present (e.g., "YES APR-123")
const parts = text.split(' ');
const response = parts[0]; // YES, NO, EXPLAIN
const approvalId = parts[1] || 'unknown';

return {
  json: {
    response: response,
    approval_id: approvalId,
    user_id: userId,
    chat_id: chatId,
    timestamp: new Date().toISOString(),
    is_yes: response === 'YES',
    is_no: response === 'NO',
    is_explain: response === 'EXPLAIN',
    original_message: text
  }
};

